import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { AboutPage } from './pages/AboutPage';
import { ContactPage } from './pages/ContactPage';
import { HomePage } from './pages/HomePage';



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      coordinates: null,
      locationInput: '',
      showMap: false,
      selectedFile: null,
      setSeletedFile: null,
    };
  }

  onFileChange = (event) => {
    this.setState({
        selectedFile: event.target.files[0],
    });
  };

  handleInputChange = (event) => {
    this.setState({ locationInput: event.target.value });
  };

  onFileUpload = async () => {
    // const { locationInput } = this.state;

    
    // Update the formData object
      
      // Details of the uploaded file
      console.log(this.state.selectedFile);
      
    try {
      const formData = new FormData();

      formData.append(
        "myFile",
        this.state.selectedFile,
        this.state.selectedFile.name
        );

      const response = await fetch('http://localhost:5000', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const responseData = await response.json();
        console.log('Response from Flask:', responseData);
        // Handle response data as needed
      } else {
        throw new Error('Failed to send data to Flask');
      }
    } catch (error) {
      console.error('Error while sending data to Flask:', error);
      // Handle error
    }


    // fetch(`http://127.0.0.1:3000/sun_data_route/${encodeURIComponent(locationInput)}`)
    //   .then(response => response.json())
    //   .then(data => {
    //     this.setState({ coordinates: data, showMap: true });
    //   })
    //   .catch(error => {
    //     console.error('Error!!!:', error);
    //   });
    // console.log('Fetching data...');
  };

  clickMap = () => {
    this.setState({ showMap: true });
  };

  render() {
    const { coordinates, locationInput, showMap } = this.state;

    return (
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/about">About</Link>
              </li>
              <li>
                <Link to="/contact">Contact</Link>
              </li>
            </ul>
          </nav>

          <Routes>
            <Route
              path="/"
              element={
                <HomePage
                  coordinates={coordinates}
                  locationInput={locationInput}
                  showMap={showMap}
                  onFileChange={this.onFileChange}
                  fetchFunction={this.onFileUpload}
                  clickMap={this.clickMap}
                />
              }
            />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />
          </Routes>
        </div>
      </Router>
    );
  }
}

export default App;
